import React, { useEffect, useState } from "react";
import "./App.css";
import ReactMapGL, { Marker, Popup } from "react-map-gl";
import { Room, Star } from "@material-ui/icons";
import axios from "axios";
function App() {
  const [viewport, setViewport] = useState({
    width: "100vw",
    height: "100vh",
    latitude: 48.7577,
    longitude: 2.2365,
    zoom: 4,
  });
  const [pins, setPins] = useState([]);
  const REACT_APP_MAPBOX_ACCESS_TOKEN = `pk.eyJ1IjoidmlwMjg1MjciLCJhIjoiY2t0MGZ0YXAzMDAyMjJvbnpob2p4YWdjMyJ9.sFXzc2nqnpaDI0O50YsBgA`;
  useEffect(() => {
    const getAllPin = async () => {
      try {
        const allPins = await axios.get("/pins");
        console.log(allPins);
        setPins(allPins.data);
      } catch (error) {
        console.log(error);
      }
    };
    getAllPin();
  }, []);
  return (
    <div className="App">
      <ReactMapGL
        {...viewport}
        mapboxApiAccessToken={REACT_APP_MAPBOX_ACCESS_TOKEN}
        onViewportChange={(nextViewport) => setViewport(nextViewport)}
        mapStyle="mapbox://styles/safak/cknndpyfq268f17p53nmpwira"
      >
        {pins.map((p) => (
          <>
            <Marker
              latitude={48.85647}
              longitude={2.23658}
              offsetLeft={-20}
              offsetTop={-10}
            >
              <Room />
            </Marker>
            <Popup
              latitude={48.85647}
              longitude={2.23658}
              closeButton={true}
              closeOnClick={false}
              anchor="top"
            >
              <div className="card">
                <label>Place</label>
                <h4 className="place">Eiffel Tower</h4>
                <label>Review</label>
                <p>Beautiful place. I like it.</p>
                <label>Rating</label>
                <div className="stars">
                  <Star />
                  <Star />
                  <Star />
                  <Star />
                  <Star />
                </div>
                <label className="username">Information</label>
                <span className="username">
                  Create By <b>Mohi</b>
                </span>
                <span className="date">1 hour ago</span>
              </div>
            </Popup>
          </>
        ))}
      </ReactMapGL>
    </div>
  );
}

export default App;
